import { LOAD_TITLES } from '../actionTypes';

function sortByVote(posts) {
  return posts.sort((a, b) => b.votes - a.votes);
}

function titlesReducer(state = [], action) {
  switch (action.type) {
    case LOAD_TITLES:
      return [...action.titles];

    default:
      return state;
  }
}

export default titlesReducer;
