import {
  ADD_COMMENT,
  DELETE_COMMENT,
  ADD_POST,
  EDIT_POST,
  DELETE_POST
} from './actionTypes';

function rootReducer(state = { posts: {} }, action) {
  console.log('reducer ran; state & action:', state, action);

  switch (action.type) {
    case ADD_POST:
      return { posts: { ...state.posts, [action.post.id]: action.post } };

    case EDIT_POST:
      return { posts: { ...state.posts, [action.post.id]: action.post } };

    case DELETE_POST:
      let { [action.id]: undefined, ...newObjects } = state.posts;
      return { posts: newObjects };

    case ADD_COMMENT:
      return { ...state, count: state.count - 1 };

    case DELETE_COMMENT:
      return { ...state, count: state.count - 1 };

    default:
      return state;
  }
}

export default rootReducer;
