import React, { Component } from 'react';

class Post extends Component {
  constructor(props) {
    super(props);
    this.deletePost = this.handleSubmit.bind(this);
  }

  render() {
    let post = this.props.posts.find(
      post => post.id === this.props.match.params.id
    );
    return (
      <div>
        <h1>{post.postTitle}</h1>
        <button onClick={this.props.history(`/edit/${post.id}`)}>Edit</button>
        <button>Delete</button>
        <i>{post.postDescription}</i>
        <p>{post.postBody}</p>
      </div>
    );
  }
}

export default Post;
