import React, { Component } from 'react';

class Post extends Component {
  render() {
    let post = this.props.posts.find(
      post => post.id === this.props.match.params.id
    );
    return (
      <div>
        <h1>{post.postTitle}</h1>
        <button>Edit</button>
        <i>{post.postDescription}</i>
        <p>{post.postBody}</p>
      </div>
    );
  }
}

export default Post;
