import React, { Component } from 'react';
import uuid from 'uuid/v4';

//Displays a list of comments associated with the parent Post
class CommentList extends Component {
  constructor(props) {
    super(props);
    this.state = { comment: '' };
    this.handleChange = this.handleChange.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(evt) {
    this.setState({ comment: evt.target.value });
  }

  handleSubmit(evt) {
    console.log(this.props);
    evt.preventDefault();
    let comment = { text: this.state.comment, id: uuid() };
    this.props.addComment(comment, this.props.postId);
    this.setState({ comment: '' });
  }

  render() {
    let comments;
    if (this.props.comments) {
      comments = this.props.comments.map(comment => {
        return (
          <li>
            <button
              onClick={() =>
                this.props.deleteComment(comment.id, this.props.post.id)
              }
            >
              X
            </button>
            {comment.text}
          </li>
        );
      });
    }

    return (
      <div>
        <ul>{comments ? comments : ''}</ul>
        <form onSubmit={this.handleSubmit}>
          <label htmlFor="comment" />
          <input
            onChange={this.handleChange}
            type="text"
            name="comment"
            value={this.state.comment}
            placeholder="New comment"
          />
          <button>Add</button>
        </form>
      </div>
    );
  }
}

export default CommentList;
