import React, { Component } from 'react';
import BlogForm from '../Components/BlogForm';
import { connect } from 'react-redux';
import { addPost, editPost } from '../actionCreators';

class FormContainer extends Component {
  handleCancel() {
    redirect;
  }

  handleSumit() {
    this.props.isEdit ? this.props.editPost() : this.props.addPost();
    this.props.history.push('/');
  }

  render() {
    return (
      <div>
        <BlogForm
          post={this.props.post}
          submit={this.props.isEdit ? this.props.editPost : this.props.addPost}
        />
      </div>
    );
  }
}

function mapStateToProps(reduxState) {
  let post;
  if (this.props) {
    post = reduxState.posts[this.props.match.params.postid];
  }
  return {
    post
  };
}

const connectToReduxStore = connect(
  mapStateToProps,
  { addPost, editPost }
);

export default connectToReduxStore(FormContainer);
